import { useState } from "react";
//?FUNCION VALIDADORA: SE VA A FIJAR SI EL EMAIL ESTA BIEN
//1- El email tiene algo?

const validate = (form, errors, setErrors) => {
  //?Valida si el email esta vacio
  if (!form.email) {
    setErrors({ ...errors, email: "Email vació" });
  } else {
    setErrors({ ...errors, email: "" });
  }
  //?Valida si el email es correcto
  if (/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{3})+$/.test(form.email))
    setErrors({ ...errors, email: "" }); //es el formato de un mail@mail.com
};

//!--------------------------COMIENZA FORM----------------------------------------//

const Form = () => {
  //?ESTADO DE FORMULARIO
  const [form, setForm] = useState({
    email: "",
    password: "",
  });
  //?ESTADO DE ERRORES DEl formulario
  const [errors, setErrors] = useState({
    email: "",
    password: "",
  });
  //?FUNCIÓN ONCHANGE: ENCARGADA DE ALMACENAR LO ESCRITO EN EL ESTADO
  const changeHandler = (event) => {
    //preguntar de donde viene el evento, eso va a depender en cual propiedad del estado va a almacenar
    const property = event.target.name;
    const value = event.target.value;
    setForm({ ...form, [property]: value }); //*cambio el estado ... esto demora
    // validate(form, errors, setErrors); //*des esta manera se maneja un estado viejo
    validate({ ...form, [property]: value }, errors, setErrors);
  };

  //?FUNCIÓN SUBMITHANDLER: maneja el submit del form
  const submitHandler = (event) => {
    event.preventDefault();
    alert("login exitoso");
  };

  return (
    <form onSubmit={submitHandler}>
      <div>
        <label htmlFor="email">email</label>
        <input
          type="text"
          name="email"
          value={form.email}
          onChange={changeHandler}
        />
      </div>
      <div>
        <label htmlFor="password">Password</label>
        <input
          type="text"
          name="password"
          value={form.password}
          onChange={changeHandler}
        />
      </div>
      <button type="submit">Login</button>
    </form>
  );
};
export default Form;
