//?FUNCION VALIDADORA: SE VA A FIJAR SI EL EMAIL ESTA BIEN
//1- El email tiene algo?
const validate = (userData, errors, setErrors) => {
  //?Valida si el email esta vacio, en el caso de que no lo este, se pregunta si es válido o no

  if (!userData.email) {
    setErrors({ ...errors, email: "Email vació" });
  } else {
    if (/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{3})+$/.test(userData.email)) {
      setErrors({ ...errors, email: "" });
    } else setErrors({ ...errors, email: "Email inválido" });
  }
};

export default validate;
