import { useState } from "react";
//?FUNCION VALIDADORA
//?ESTADO DE ERRORES DEl formulario
const [errors, setErrors] = useState({
  username: "",
  password: "",
});
const validate = (form) => {};

const Form = () => {
  //?ESTADO DE FORMULARIO

  const [form, setForm] = useState({
    username: "",
    password: "",
  });
  //?FUNCIÓN ONCHANGE: ENCARGADA DE ALMACENAR LO ESCRITO EN EL ESTADO
  const changeHandler = (event) => {
    //preguntar de donde viene el evento, eso va a depender en cual propiedad del estado va a almacenar
    const property = event.target.name;
    const value = event.target.value;
    setForm({ ...form, [property]: value });
    validate(form);
  };

  //?FUNCIÓN SUBMITHANDLER: maneja el submit del form
  const submitHandler = (event) => {
    event.preventDefault();
    alert("login exitoso");
  };

  return (
    <form onSubmit={submitHandler}>
      <div>
        <label htmlFor="username">Username</label>
        <input
          type="text"
          name="username"
          value={form.username}
          onChange={changeHandler}
        />
      </div>
      <div>
        <label htmlFor="password">Password</label>
        <input
          type="text"
          name="password"
          value={form.password}
          onChange={changeHandler}
        />
      </div>
      <button type="submit">Login</button>
    </form>
  );
};
export default Form;
