import "./App.css";
import Cards from "./components/Cards.jsx";
import { useState } from "react";
// import characters from "./data.js";
import Nav from "./components/Nav";
import { Routes, Route } from "react-router-dom";

function App() {
  const [characters, setCharacters] = useState([
    {
      name: "Morty Smith",
      species: "Human",
      gender: "Male",
      image: "https://rickandmortyapi.com/api/character/avatar/2.jpeg",
    },
  ]);
  const onSearch = (id) => {
    //!Se declara la url y la key necesaria para acceder a la api

    const URL_BASE = "https://be-a-rym.up.railway.app/api";
    const KEY = "3e8cca96246b.21c19451ba2b8a27ed20";

    fetch(`${URL_BASE}/character/${id}?key=${KEY}`)
      .then((response) => response.json())
      .then((data) => {
        if (data.name && !characters.find((char) => char.id === data.id)) {
          setCharacters((oldsChars) => [...oldsChars, data]);
        } else {
          alert("Algo salio mal");
        }
      });
  };
  //!Aca no estamos modificando el array original sino que me devuelve un array nuevo
  const onClose = (id) => {
    setCharacters(characters.filter((char) => char.id !== id));
  };
  return (
    <div className="App" style={{ padding: "25px" }}>
      <hr />
      <Nav onSearch={onSearch} />
      {/* se declaran las rutas */}
      <Routes>
        <Route path="/home">
          <Cards characters={characters} onClose={onClose} />
        </Route>

        <Route path="/about"></Route>
        <Route path="/detail/:detailId"></Route>
        <hr />
      </Routes>
    </div>
  );
}

export default App;
