//?FUNCION VALIDADORA: SE VA A FIJAR SI EL EMAIL ESTA BIEN
//1- El email tiene algo?
const validate = (userData, errors, setErrors) => {
  //?Valida si el email esta vacio, si cumple el formato y si no pasa de los 35 caracteres
  if (!userData.email) {
    setErrors({ ...errors, email: "Email vació" });
  } else {
    if (
      /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{3})+$/.test(userData.email) &&
      userData.email.length <= 35
    ) {
      setErrors({ ...errors, email: "" });
    } else setErrors({ ...errors, email: "Email inválido" });
  }

  //? Valida el formato de contraseña
  const passwordLg = userData.password.length;
  const password = userData.password;
  if (!(passwordLg <= 10 && passwordLg >= 6) || !/[0-9]/.test(password)) {
    setErrors({ ...errors, password: "Contraseña inválida" });
  } else setErrors({ ...errors, password: "" });
  //1-la contraseña tiene que tener entre 6 y 10 caracteres
};

export default validate;
